describe("Attributes += methods",function(){var e,t;beforeEach(function(){e=Snap(100,100);t=e.rect(10,10,50,50)});afterEach(function(){e.remove()});it("+=10",function(){t.attr({x:"+=10"});expect(t.node.getAttribute("x")).to.be("20")});it("-=10",function(){t.attr({x:"-=10"});expect(t.node.getAttribute("x")).to.be("0")});it("*=2",function(){t.attr({x:"*=2"});expect(t.node.getAttribute("x")).to.be("20")});it("/=2",function(){t.attr({x:"/=2"});expect(t.node.getAttribute("x")).to.be("5")});it("+=1em",function(){var n=e.rect(0,0,"1em","1em");n=n.getBBox().w;t.attr({x:"+=1em"});expect(t.node.getAttribute("x")).to.eql(10+n)});it("-=.3em",function(){var n=e.rect(0,0,"1em","1em");n=n.getBBox().w;t.attr({x:"-=.3em"});expect((+t.node.getAttribute("x")).toFixed(2)).to.eql((10-n*.3).toFixed(2))})});