describe("Attributes",function(){function n(e){var n={};return function(){n[e]="#fc0";t.attr(n);expect(t.node.getAttribute(e)).to.be("#ffcc00");n[e]="rgb(255, 204, 0)";t.attr(n);expect(t.node.getAttribute(e)).to.be("#ffcc00");n[e]="hsl(0.1333, 1, .5)";t.attr(n);expect(t.node.getAttribute(e)).to.be("#ffcc00");n[e]="hsb(0.1333, 1, 1)";t.attr(n);expect(t.node.getAttribute(e)).to.be("#ffcc00");n[e]="rgba(255, 204, 0, .5)";t.attr(n);expect(t.node.getAttribute(e)).to.be("rgba(255,204,0,0.5)");n[e]="hsla(0.1333, 1, .5, .5)";t.attr(n);expect(t.node.getAttribute(e)).to.be("rgba(255,204,0,0.5)");n[e]="hsba(0.1333, 1, 1, .5)";t.attr(n);expect(t.node.getAttribute(e)).to.be("rgba(255,204,0,0.5)")}}function r(e){var n={};return function(){function r(){return Snap.color(t.node.getAttribute(e)).hex}n[e]="#fc0";t.attr(n);expect(r()).to.be("#ffcc00");n[e]="rgb(255, 204, 0)";t.attr(n);expect(r()).to.be("#ffcc00");n[e]="hsl(0.1333, 1, .5)";t.attr(n);expect(r()).to.be("#ffcc00");n[e]="hsb(0.1333, 1, 1)";t.attr(n);expect(r()).to.be("#ffcc00");n[e]="rgba(255, 204, 0, .5)";t.attr(n);expect(r()).to.be("#ffcc00");n[e]="hsla(0.1333, 1, .5, .5)";t.attr(n);expect(r()).to.be("#ffcc00");n[e]="hsba(0.1333, 1, 1, .5)";t.attr(n);expect(r()).to.be("#ffcc00")}}var e,t;beforeEach(function(){e=Snap(100,100);t=e.rect(10,10,50,50)});afterEach(function(){e.remove()});it("sets fill",n("fill"));it("sets stroke",r("stroke"));it("circle core attributes",function(){var t=e.circle(10,20,30);expect(t.node.getAttribute("cx")).to.be("10");expect(t.node.getAttribute("cy")).to.be("20");expect(t.node.getAttribute("r")).to.be("30");t.attr({cx:40,cy:50,r:"5%"});expect(t.node.getAttribute("cx")).to.be("40");expect(t.node.getAttribute("cy")).to.be("50");expect(t.node.getAttribute("r")).to.be("5%")});it("rect core attributes",function(){var t=e.rect(10,20,30,40);expect(t.node.getAttribute("x")).to.be("10");expect(t.node.getAttribute("y")).to.be("20");expect(t.node.getAttribute("width")).to.be("30");expect(t.node.getAttribute("height")).to.be("40");t.attr({x:40,y:50,width:"5%",height:"6%",r:10});expect(t.node.getAttribute("x")).to.be("40");expect(t.node.getAttribute("y")).to.be("50");expect(t.node.getAttribute("width")).to.be("5%");expect(t.node.getAttribute("height")).to.be("6%");expect(t.node.getAttribute("rx")).to.be("10");expect(t.node.getAttribute("ry")).to.be("10")});it("ellipse core attributes",function(){var t=e.ellipse(10,20,30,40);expect(t.node.getAttribute("cx")).to.be("10");expect(t.node.getAttribute("cy")).to.be("20");expect(t.node.getAttribute("rx")).to.be("30");expect(t.node.getAttribute("ry")).to.be("40");t.attr({cx:40,cy:50,rx:"5%",ry:"6%"});expect(t.node.getAttribute("cx")).to.be("40");expect(t.node.getAttribute("cy")).to.be("50");expect(t.node.getAttribute("rx")).to.be("5%");expect(t.node.getAttribute("ry")).to.be("6%")});it("path core attributes",function(){var t=e.path("M10,10 110,10");expect(t.node.getAttribute("d")).to.be("M10,10 110,10");t.attr({d:"M10,10 210,10"});expect(t.node.getAttribute("d")).to.be("M10,10 210,10");t.attr({path:"M10,10 310,10"});expect(t.node.getAttribute("d")).to.be("M10,10 310,10")});it("text core attributes",function(){var t=e.text(10,15,"testing");expect(t.node.getAttribute("x")).to.be("10");expect(t.node.getAttribute("y")).to.be("15");expect(t.node.textContent).to.be("testing");t.attr({x:20,y:25,text:"texting"});expect(t.node.getAttribute("x")).to.be("20");expect(t.node.getAttribute("y")).to.be("25");expect(t.node.textContent).to.be("texting")});it("line core attributes",function(){var t=e.line(10,15,110,17);expect(t.node.getAttribute("x1")).to.be("10");expect(t.node.getAttribute("y1")).to.be("15");expect(t.node.getAttribute("x2")).to.be("110");expect(t.node.getAttribute("y2")).to.be("17");t.attr({x1:20,y1:25,x2:220,y2:27});expect(t.node.getAttribute("x1")).to.be("20");expect(t.node.getAttribute("y1")).to.be("25");expect(t.node.getAttribute("x2")).to.be("220");expect(t.node.getAttribute("y2")).to.be("27")});it("polyline core attributes",function(){var t=e.polyline(10,15,20,25,30,35);expect(t.node.getAttribute("points")).to.be("10,15,20,25,30,35");t.attr({points:[20,25,30,35,40,45]});expect(t.node.getAttribute("points")).to.be("20,25,30,35,40,45")});it("polygon core attributes",function(){var t=e.polygon(10,15,20,25,30,35);expect(t.node.getAttribute("points")).to.be("10,15,20,25,30,35");t.attr({points:[20,25,30,35,40,45]});expect(t.node.getAttribute("points")).to.be("20,25,30,35,40,45")})});