describe("Class methods",function(){var e,t;beforeEach(function(){e=Snap(100,100);t=e.rect(10,10,50,50)});afterEach(function(){e.remove()});it("elproto.addClass one",function(){t.addClass("one");expect(t.node.className.baseVal).to.be("one")});it("elproto.addClass two",function(){t.addClass("one two");expect(t.node.className.baseVal).to.be("one two")});it("elproto.addClass two (spacing)",function(){t.addClass("	one   two ");expect(t.node.className.baseVal).to.be("one two")});it("elproto.addClass three",function(){t.addClass("one  two three");expect(t.node.className.baseVal).to.be("one two three")});it("elproto.removeClass 1",function(){t.addClass("one  two three");t.removeClass("two");expect(t.node.className.baseVal).to.be("one three")});it("elproto.removeClass 2",function(){t.addClass("one  two three");t.removeClass("two one");expect(t.node.className.baseVal).to.be("three")});it("elproto.hasClass",function(){t.addClass("one  two three");expect(t.hasClass("two")).to.be(!0)});it("elproto.toggleClass 1",function(){t.addClass("one  two three");t.toggleClass("two one");expect(t.node.className.baseVal).to.be("three")});it("elproto.toggleClass 2",function(){t.addClass("one  three");t.toggleClass("two one",!1);expect(t.node.className.baseVal).to.be("three")});it("elproto.toggleClass 3",function(){t.addClass("one three");t.toggleClass("two one",!0);expect(t.node.className.baseVal).to.be("one three two")})});