describe("Colours",function(){it("parses hex colour",function(){expect(Snap.color("#fC0").hex).to.be("#ffcc00")});it("parses RGB",function(){var e=Snap.color("rgb(255, 204, 0)");expect(e.hex).to.be("#ffcc00");expect(e.r).to.be(255);expect(e.g).to.be(204);expect(e.b).to.be(0)});it("parses RGB - %",function(){var e=Snap.color("rgb(100%, 80%, 0%)");expect(e.hex).to.be("#ffcc00");expect(e.r).to.be(255);expect(e.g).to.be(204);expect(e.b).to.be(0)});it("parses HSL",function(){var e=Snap.color("hsl(0.1333, 1, .5)");expect(e.hex).to.be("#ffcc00");expect(e.h.toFixed(3)).to.be("0.133");expect(e.s).to.be(1);expect(e.l).to.be(.5)});it("parses HSL - %",function(){var e=Snap.color("hsl(13.33%, 100%, 50%)");expect(e.hex).to.be("#ffcc00");expect(e.h.toFixed(3)).to.be("0.133");expect(e.s).to.be(1);expect(e.l).to.be(.5)});it("parses HSB",function(){var e=Snap.color("hsb(0.1333, 1, 1)");expect(e.hex).to.be("#ffcc00");expect(e.h.toFixed(3)).to.be("0.133");expect(e.s).to.be(1);expect(e.v).to.be(1)});it("parses HSB - %",function(){var e=Snap.color("hsb(13.33%, 100%, 100%)");expect(e.hex).to.be("#ffcc00");expect(e.h.toFixed(3)).to.be("0.133");expect(e.s).to.be(1);expect(e.v).to.be(1)});it("parses RGBA",function(){var e=Snap.color("rgba(255, 204, 0, .75)");expect(e.hex).to.be("#ffcc00");expect(e.r).to.be(255);expect(e.g).to.be(204);expect(e.b).to.be(0);expect(e.opacity).to.be(.75)});it("parses HSLA",function(){var e=Snap.color("hsla(0.1333, 1, .5, .5)");expect(e.hex).to.be("#ffcc00");expect(e.r).to.be(255);expect(e.g).to.be(204);expect(e.b).to.be(0);expect(e.opacity).to.be(.5)});it("parses HSBA",function(){var e=Snap.color("hsba(0.1333, 1, 1, .5)");expect(e.hex).to.be("#ffcc00");expect(e.r).to.be(255);expect(e.g).to.be(204);expect(e.b).to.be(0);expect(e.opacity).to.be(.5)});it("parses names",function(){var e=Snap.color("DodgerBlue");expect(e.hex).to.be("#1e90ff");e=Snap.color("FireBrick");expect(e.hex).to.be("#b22222");e=Snap.color("MintCream");expect(e.hex).to.be("#f5fffa")});it("parses to RGB",function(){var e=function(e){var t=Snap.getRGB(e);expect(t.hex).to.be("#ffcc00");expect(t.r).to.be(255);expect(t.g).to.be(204);expect(t.b).to.be(0);expect(t.error).to.not.be(!0)};e("#fc0");e("#ffcc00");e("rgb(255, 204, 0)");e("rgb(100%, 80%, 0%)");e("hsb(0.1333, 1, 1)");e("hsb(13.33%, 100%, 100%)");e("hsl(0.1333, 1, .5)");e("hsl(13.33%, 100%, 50%)");e("rgba(255, 204, 0, .75)");e("hsla(0.1333, 1, .5, .5)");e("hsba(0.1333, 1, 1, .5)");var t=Snap.getRGB("DodgerBlue");expect(t.hex).to.be("#1e90ff");expect(t.r).to.be(30);expect(t.g).to.be(144);expect(t.b).to.be(255);expect(t.error).to.not.be(!0);t=Snap.getRGB("foobar");expect(!!t.error).to.be(!0);t=Snap.getRGB("#zzz");expect(!!t.error).to.be(!0);t=Snap.getRGB("rgb(255)");expect(!!t.error).to.be(!0);t=Snap.getRGB("hsl(0, 0, 0");expect(!!t.error).to.be(!0);t=Snap.getRGB("rab(0, 0, 0)");expect(!!t.error).to.be(!0)});it("creates hsb",function(){var e=Snap.hsb(.13333,1,1);expect(e).to.be("#ffcc00");e=Snap.hsb(0,.5,.5);expect(e).to.be("#804040")});it("creates rgb from hsb",function(){var e=Snap.hsb2rgb(.13333,1,1);expect(e.r).to.be(255);expect(e.g).to.be(204);expect(e.b).to.be(0);expect(e.hex).to.be("#ffcc00");e=Snap.hsb2rgb(0,.5,.5);expect(e.r).to.be(128);expect(e.g).to.be(64);expect(e.b).to.be(64);expect(e.hex).to.be("#804040")});it("creates hsl",function(){var e=Snap.hsl(.13333,1,.5);expect(e).to.be("#ffcc00");e=Snap.hsl(0,1,.75);expect(e).to.be("#ff8080")});it("creates rgb from hsl",function(){var e=Snap.hsl2rgb(.13333,1,.5);expect(e.r).to.be(255);expect(e.g).to.be(204);expect(e.b).to.be(0);expect(e.hex).to.be("#ffcc00");e=Snap.hsl2rgb(0,1,.75);expect(e.r).to.be(255);expect(e.g).to.be(128);expect(e.b).to.be(128);expect(e.hex).to.be("#ff8080")});it("creates rgb",function(){var e=Snap.rgb(255,204,0);expect(e).to.be("#ffcc00");e=Snap.rgb(64,128,255);expect(e).to.be("#4080ff")});it("creates hsb from rgb",function(){var e=Snap.rgb2hsb(255,204,0);expect(e.h.toFixed(3)).to.be("0.133");expect(e.s).to.be(1);expect(e.b).to.be(1);e=Snap.rgb2hsb(128,64,64);expect(e.h).to.be(0);expect(e.s.toFixed(1)).to.be("0.5");expect(e.b.toFixed(1)).to.be("0.5")});it("creates hsl from rgb",function(){var e=Snap.rgb2hsl(255,204,0);expect(e.h.toFixed(3)).to.be("0.133");expect(e.s).to.be(1);expect(e.l).to.be(.5);e=Snap.rgb2hsl(255,128,128);expect(e.h).to.be(0);expect(e.s).to.be(1);expect(e.l.toFixed(2)).to.be("0.75")})});