describe("Filter methods",function(){it("Snap.filter.blur",function(){var e=Snap.filter.blur(3);expect(e).to.be('<feGaussianBlur stdDeviation="3"/>');e=Snap.filter.blur(.123,8);expect(e).to.be('<feGaussianBlur stdDeviation="0.123,8"/>')});it("Snap.filter.brightness",function(){var e=Snap.filter.brightness(.3);expect(e).to.be('<feComponentTransfer><feFuncR type="linear" slope="0.3"/><feFuncG type="linear" slope="0.3"/><feFuncB type="linear" slope="0.3"/></feComponentTransfer>');e=Snap.filter.brightness(1);expect(e).to.be('<feComponentTransfer><feFuncR type="linear" slope="1"/><feFuncG type="linear" slope="1"/><feFuncB type="linear" slope="1"/></feComponentTransfer>')});it("Snap.filter.contrast",function(){var e=Snap.filter.contrast(.1);expect(e).to.be('<feComponentTransfer><feFuncR type="linear" slope="0.1" intercept="0.45"/><feFuncG type="linear" slope="0.1" intercept="0.45"/><feFuncB type="linear" slope="0.1" intercept="0.45"/></feComponentTransfer>');e=Snap.filter.contrast(3);expect(e).to.be('<feComponentTransfer><feFuncR type="linear" slope="3" intercept="-1"/><feFuncG type="linear" slope="3" intercept="-1"/><feFuncB type="linear" slope="3" intercept="-1"/></feComponentTransfer>')});it("Snap.filter.grayscale",function(){var e=Snap.filter.grayscale(.5);expect(e).to.be('<feColorMatrix type="matrix" values="0.6063000000000001 0.3576 0.0361 0 0 0.1063 0.8575999999999999 0.0361 0 0 0.1063 0.3576 0.5361 0 0 0 0 0 1 0"/>');e=Snap.filter.grayscale(1);expect(e).to.be('<feColorMatrix type="matrix" values="0.2126 0.7152 0.0722 0 0 0.2126 0.7152 0.0722 0 0 0.2126 0.7152 0.0722 0 0 0 0 0 1 0"/>')});it("Snap.filter.hueRotate",function(){var e=Snap.filter.hueRotate(180);expect(e).to.be('<feColorMatrix type="hueRotate" values="180"/>');e=Snap.filter.hueRotate(90);expect(e).to.be('<feColorMatrix type="hueRotate" values="90"/>')});it("Snap.filter.hueRotate",function(){var e=Snap.filter.hueRotate(180);expect(e).to.be('<feColorMatrix type="hueRotate" values="180"/>');e=Snap.filter.hueRotate(90);expect(e).to.be('<feColorMatrix type="hueRotate" values="90"/>')});it("Snap.filter.invert",function(){var e=Snap.filter.invert(.6);expect(e).to.be('<feComponentTransfer><feFuncR type="table" tableValues="0.6 0.4"/><feFuncG type="table" tableValues="0.6 0.4"/><feFuncB type="table" tableValues="0.6 0.4"/></feComponentTransfer>');e=Snap.filter.invert(1);expect(e).to.be('<feComponentTransfer><feFuncR type="table" tableValues="1 0"/><feFuncG type="table" tableValues="1 0"/><feFuncB type="table" tableValues="1 0"/></feComponentTransfer>')});it("Snap.filter.saturate",function(){var e=Snap.filter.saturate(.3);expect(e).to.be('<feColorMatrix type="saturate" values="0.7"/>');e=Snap.filter.saturate(1);expect(e).to.be('<feColorMatrix type="saturate" values="0"/>')});it("Snap.filter.sepia",function(){var e=Snap.filter.sepia(.3);expect(e).to.be('<feColorMatrix type="matrix" values="0.8179 0.23070000000000002 0.0567 0 0 0.10470000000000002 0.9058 0.050400000000000014 0 0 0.0816 0.1602 0.7393 0 0 0 0 0 1 0"/>');e=Snap.filter.sepia(1);expect(e).to.be('<feColorMatrix type="matrix" values="0.393 0.769 0.189 0 0 0.349 0.686 0.168 0 0 0.272 0.534 0.131 0 0 0 0 0 1 0"/>')});it("Snap.filter.shadow - dx & dy",function(){var e=Snap.filter.shadow(5,5);expect(e).to.be('<feGaussianBlur in="SourceAlpha" stdDeviation="4"/><feOffset dx="5" dy="5" result="offsetblur"/><feFlood flood-color="#000000"/><feComposite in2="offsetblur" operator="in"/><feComponentTransfer><feFuncA type="linear" slope="1"/></feComponentTransfer><feMerge><feMergeNode/><feMergeNode in="SourceGraphic"/></feMerge>');e=Snap.filter.shadow(-1,3);expect(e).to.be('<feGaussianBlur in="SourceAlpha" stdDeviation="4"/><feOffset dx="-1" dy="3" result="offsetblur"/><feFlood flood-color="#000000"/><feComposite in2="offsetblur" operator="in"/><feComponentTransfer><feFuncA type="linear" slope="1"/></feComponentTransfer><feMerge><feMergeNode/><feMergeNode in="SourceGraphic"/></feMerge>')});it("Snap.filter.shadow - dx & dy, blur",function(){var e=Snap.filter.shadow(5,5,5);expect(e).to.be('<feGaussianBlur in="SourceAlpha" stdDeviation="5"/><feOffset dx="5" dy="5" result="offsetblur"/><feFlood flood-color="#000000"/><feComposite in2="offsetblur" operator="in"/><feComponentTransfer><feFuncA type="linear" slope="1"/></feComponentTransfer><feMerge><feMergeNode/><feMergeNode in="SourceGraphic"/></feMerge>');e=Snap.filter.shadow(-1,3,10);expect(e).to.be('<feGaussianBlur in="SourceAlpha" stdDeviation="10"/><feOffset dx="-1" dy="3" result="offsetblur"/><feFlood flood-color="#000000"/><feComposite in2="offsetblur" operator="in"/><feComponentTransfer><feFuncA type="linear" slope="1"/></feComponentTransfer><feMerge><feMergeNode/><feMergeNode in="SourceGraphic"/></feMerge>')});it("Snap.filter.shadow - dx & dy, color",function(){var e=Snap.filter.shadow(5,5,"#F00");expect(e).to.be('<feGaussianBlur in="SourceAlpha" stdDeviation="4"/><feOffset dx="5" dy="5" result="offsetblur"/><feFlood flood-color="#ff0000"/><feComposite in2="offsetblur" operator="in"/><feComponentTransfer><feFuncA type="linear" slope="#F00"/></feComponentTransfer><feMerge><feMergeNode/><feMergeNode in="SourceGraphic"/></feMerge>');e=Snap.filter.shadow(-1,3,"hsla(128deg, 50%, 50%, 0.8)");expect(e).to.be('<feGaussianBlur in="SourceAlpha" stdDeviation="4"/><feOffset dx="-1" dy="3" result="offsetblur"/><feFlood flood-color="rgba(64,191,81,0.8)"/><feComposite in2="offsetblur" operator="in"/><feComponentTransfer><feFuncA type="linear" slope="hsla(128deg, 50%, 50%, 0.8)"/></feComponentTransfer><feMerge><feMergeNode/><feMergeNode in="SourceGraphic"/></feMerge>')});it("Snap.filter.shadow - dx & dy, blur & color",function(){var e=Snap.filter.shadow(5,5,5,"#F00");expect(e).to.be('<feGaussianBlur in="SourceAlpha" stdDeviation="5"/><feOffset dx="5" dy="5" result="offsetblur"/><feFlood flood-color="#ff0000"/><feComposite in2="offsetblur" operator="in"/><feComponentTransfer><feFuncA type="linear" slope="1"/></feComponentTransfer><feMerge><feMergeNode/><feMergeNode in="SourceGraphic"/></feMerge>');e=Snap.filter.shadow(-1,3,10,"hsla(128deg, 50%, 50%, 0.8)");expect(e).to.be('<feGaussianBlur in="SourceAlpha" stdDeviation="10"/><feOffset dx="-1" dy="3" result="offsetblur"/><feFlood flood-color="rgba(64,191,81,0.8)"/><feComposite in2="offsetblur" operator="in"/><feComponentTransfer><feFuncA type="linear" slope="1"/></feComponentTransfer><feMerge><feMergeNode/><feMergeNode in="SourceGraphic"/></feMerge>')})});