describe("Matrix methods",function(){it("Matrix.add - matrix",function(){var e=new Snap.Matrix(1,0,0,1,5,5),t=new Snap.Matrix(1,0,0,1,10,10),n=e.add(t);expect(n).to.eql({a:1,b:0,c:0,d:1,e:15,f:15});var r=new Snap.Matrix(0,1,-1,0,0,0),i=new Snap.Matrix(0,1,-1,0,0,0);n=r.add(i);expect(n).to.eql({a:-1,b:0,c:0,d:-1,e:0,f:0})});it("Matrix.add - numbers",function(){var e=new Snap.Matrix(1,0,0,1,5,5),t=e.add(1,0,0,1,10,10);expect(t).to.eql({a:1,b:0,c:0,d:1,e:15,f:15})});it("Matrix.clone",function(){var e=new Snap.Matrix(1,2,3,4,5,6),t=e.clone();expect(t).to.not.be(e);expect(t).to.eql({a:1,b:2,c:3,d:4,e:5,f:6})});it("Matrix.invert",function(){var e=new Snap.Matrix(1,2,3,4,5,6),t=e.invert();expect(t).to.eql({a:-2,b:1,c:1.5,d:-0.5,e:1,f:-2})});it("Matrix.rotate",function(){var e=new Snap.Matrix(1,0,0,1,0,0);e.rotate(45,0,0);expect(+e.a.toFixed(3)).to.be(.707);expect(+e.b.toFixed(3)).to.be(.707);expect(+e.c.toFixed(3)).to.be(-0.707);expect(+e.d.toFixed(3)).to.be(.707);expect(+e.e.toFixed(3)).to.be(0);expect(+e.f.toFixed(3)).to.be(0)});it("Matrix.scale - x",function(){var e=new Snap.Matrix(1,0,0,1,20,30);e.scale(2);expect(e).to.eql({a:2,b:0,c:0,d:2,e:20,f:30});e.scale(.5);expect(e).to.eql({a:1,b:0,c:0,d:1,e:20,f:30})});it("Matrix.scale - x, y",function(){var e=new Snap.Matrix(1,0,0,1,20,30);e.scale(2,3);expect(e).to.eql({a:2,b:0,c:0,d:3,e:20,f:30});e.scale(.5,1);expect(e).to.eql({a:1,b:0,c:0,d:3,e:20,f:30})});it("Matrix.scale - x, y, cx, cy",function(){var e=new Snap.Matrix(1,0,0,1,20,30);e.scale(2,3,5,-5);expect(e).to.eql({a:2,b:0,c:0,d:3,e:15,f:40})});it("Matrix.split",function(){var e=new Snap.Matrix(1,0,0,1,0,0),t=e.split();expect(t.dx).to.be(0);expect(t.dy).to.be(0);expect(t.scalex).to.be(1);expect(t.scaley).to.be(1);expect(t.shear).to.be(0);expect(t.rotate).to.be(0);expect(t.isSimple).to.be(!0);e=new Snap.Matrix(1.5,0,0,.5,20,25);t=e.split();expect(t.dx).to.be(20);expect(t.dy).to.be(25);expect(t.scalex).to.be(1.5);expect(t.scaley).to.be(.5);expect(t.shear).to.be(0);expect(t.rotate).to.be(0);expect(t.isSimple).to.be(!0)});it("Matrix.toTransformString",function(){var e=new Snap.Matrix(1.5,0,0,.5,20,25),t=e.toTransformString(),n=Snap(10,10),r=n.rect(0,0,10,20);r.transform(t);var i=r.transform();expect(i.localMatrix).to.eql({a:1.5,b:0,c:0,d:.5,e:20,f:25})});it("Matrix.translate",function(){var e=new Snap.Matrix(1,0,0,1,20,30);e.translate(10,-10);expect(e).to.eql({a:1,b:0,c:0,d:1,e:30,f:20});e.translate(-1,-2);expect(e).to.eql({a:1,b:0,c:0,d:1,e:29,f:18})});it("Matrix.x",function(){var e=new Snap.Matrix(1,0,0,1,20,30),t=e.x(10,-10);expect(t).to.be(30)});it("Matrix.y",function(){var e=new Snap.Matrix(1,0,0,1,20,30),t=e.y(10,-10);expect(t).to.be(20)})});